            <a
              href="/contact"
              className="inline-flex items-center justify-center px-8 py-4 bg-transparent border-2 border-white text-white rounded-full font-semibold hover:bg-white hover:text-primary transition-all duration-300"
            >
              Contact Us
            </a>
          </div>
        </div>
      </section>
    </Layout>
  );
};

export default About;

import Layout from "@/components/layout/Layout";
import SEO from "@/components/SEO";
import { MapPin, Phone, Mail, Clock, Send } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useEffect, useState } from "react";
import api from "@/lib/api";
import Loader from "@/components/ui/Loader";

const iconMap: { [key: string]: any } = {
  MapPin,
  Phone,
  Mail,
  Clock
};

const Contact = () => {
  const [content, setContent] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const { data } = await api.get("/pages/contact");
        setContent(data.content);
      } catch (error) {
        console.error("Failed to fetch contact page content", error);
      } finally {
        setLoading(false);
      }
    };
    fetchContent();
  }, []);

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    company: "",
    country: "",
    subject: "",
    message: "",
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubjectChange = (value: string) => {
    setFormData(prev => ({ ...prev, subject: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await api.post("/inquiries", formData);
      toast({
        title: "Message Sent Successfully!",
        description: "Thank you for contacting us. We'll get back to you within 24 hours.",
      });
      setFormData({
        name: "", email: "", phone: "", company: "", country: "", subject: "", message: ""
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again later.",
        variant: "destructive"
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const defaultContactInfo = [
    {
      icon: MapPin,
      title: "Visit Us",
      details: ["123 Export Zone, Industrial Area", "Mumbai, Maharashtra 400001, India"],
    },
    {
      icon: Phone,
      title: "Call Us",
      details: ["+91 123 456 7890", "+91 098 765 4321"],
    },
    {
      icon: Mail,
      title: "Email Us",
      details: ["info@njrexim.com", "sales@njrexim.com"],
    },
    {
      icon: Clock,
      title: "Working Hours",
      details: ["Monday - Friday: 9:00 AM - 6:00 PM", "Saturday: 9:00 AM - 1:00 PM"],
    },
  ];

  if (loading && !content) return <Loader />;

  const hero = content?.hero || {
    badge: "Get In Touch",
    title: "Contact Us",
    description: "Have questions about our products or services? We're here to help. Reach out to us and we'll respond as soon as possible."
  };

  const info = content?.info;
  const contactCards = info ? [
    { icon: MapPin, title: "Visit Us", details: info.address },
    { icon: Phone, title: "Call Us", details: info.phones },
    { icon: Mail, title: "Email Us", details: info.emails },
    { icon: Clock, title: "Working Hours", details: info.workingHours },
  ] : defaultContactInfo;

  return (
    <Layout>
      <SEO
        title="Contact Us for Global Export Inquiries"
        description="Get in touch with NJR EXIM for premium agricultural product quotes, export inquiries, and international trade partnerships. We respond within 24 hours."
        canonical="/contact"
      />
      {/* Hero Section */}
      <section className="relative bg-secondary text-secondary-foreground pt-32 pb-16">
        <div className="container-custom">
          <div className="max-w-3xl">
            <span className="inline-block px-4 py-2 bg-primary/20 text-primary-foreground text-sm font-medium uppercase tracking-widest rounded-full mb-6 border border-primary/30">
              {hero.badge}
            </span>
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6">
              {hero.title.includes("Us") ? (
                <>Contact <span className="text-golden">Us</span></>
              ) : hero.title}
            </h1>
            <p className="text-lg text-secondary-foreground/80 leading-relaxed">
              {hero.description}
            </p>
          </div>
        </div>
      </section>

      {/* Contact Info Cards */}
      <section className="section-padding bg-background">
        <div className="container-custom">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-16">
            {contactCards.map((info) => (
              <div
                key={info.title}
                className="bg-card p-6 rounded-xl shadow-md hover:shadow-lg transition-all duration-300"
              >
                <div className="w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center mb-4">
                  <info.icon className="w-6 h-6 text-primary" />
                </div>
                <h3 className="text-lg font-bold text-foreground mb-3">{info.title}</h3>
                {info.details.map((detail, index) => (
                  <p key={index} className="text-sm text-muted-foreground">
                    {detail}
                  </p>
                ))}
              </div>
            ))}
          </div>

          {/* Contact Form & Map */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            {/* Form */}
            <div>
              <h2 className="text-3xl font-bold text-foreground mb-6">Send Us a Message</h2>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label htmlFor="name" className="block text-sm font-medium text-foreground mb-2">
                      Full Name *
                    </label>
                    <Input
                      id="name"
                      name="name"
                      type="text"
                      required
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="John Doe"
                      className="h-12"
                    />
                  </div>
                  <div>
                    <label htmlFor="email" className="block text-sm font-medium text-foreground mb-2">
                      Email Address *
                    </label>
                    <Input
                      id="email"
                      name="email"
                      type="email"
                      required
                      value={formData.email}
                      onChange={handleInputChange}
                      placeholder="john@example.com"
                      className="h-12"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label htmlFor="phone" className="block text-sm font-medium text-foreground mb-2">
                      Phone Number
                    </label>
                    <Input
                      id="phone"
                      name="phone"
                      type="tel"
                      value={formData.phone}
                      onChange={handleInputChange}
                      placeholder="+1 234 567 8900"
                      className="h-12"
                    />
                  </div>
                  <div>
                    <label htmlFor="company" className="block text-sm font-medium text-foreground mb-2">
                      Company Name
                    </label>
                    <Input
                      id="company"
                      name="company"
                      type="text"
                      value={formData.company}
                      onChange={handleInputChange}
                      placeholder="Your Company"
                      className="h-12"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label htmlFor="country" className="block text-sm font-medium text-foreground mb-2">
                      Country
                    </label>
                    <Input
                      id="country"
                      name="country"
                      type="text"
                      value={formData.country}
                      onChange={handleInputChange}
                      placeholder="United States"
                      className="h-12"
                    />
                  </div>
                  <div>
                    <label htmlFor="subject" className="block text-sm font-medium text-foreground mb-2">
                      Subject *
                    </label>
                    <Select value={formData.subject} onValueChange={handleSubjectChange} required>
                      <SelectTrigger className="h-12">
                        <SelectValue placeholder="Select a subject" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="product-inquiry">Product Inquiry</SelectItem>
                        <SelectItem value="quote-request">Quote Request</SelectItem>
                        <SelectItem value="partnership">Partnership Opportunity</SelectItem>
                        <SelectItem value="quality-concern">Quality Concern</SelectItem>
                        <SelectItem value="general">General Question</SelectItem>
                        <SelectItem value="other">Other</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div>
                  <label htmlFor="message" className="block text-sm font-medium text-foreground mb-2">
                    Message *
                  </label>
                  <Textarea
                    id="message"
                    name="message"
                    required
                    value={formData.message}
                    onChange={handleInputChange}
                    placeholder="Tell us how we can help you..."
                    rows={6}
                    className="resize-none"
                  />
                </div>

                <Button
                  type="submit"
                  size="lg"
                  disabled={isSubmitting}
                  className="w-full"
                >
                  {isSubmitting ? (
                    "Sending..."
                  ) : (
                    <>
                      Send Message
                      <Send className="w-5 h-5 ml-2" />
                    </>
                  )}
                </Button>
              </form>
            </div>

            {/* Map & Info */}
            <div className="space-y-6">
              <div>
                <h2 className="text-3xl font-bold text-foreground mb-6">Our Location</h2>
                <div className="relative w-full h-[400px] rounded-2xl overflow-hidden shadow-xl">
                  <iframe
                    src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d241317.11609823277!2d72.74109995709657!3d19.082177799999996!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3be7c6306644edc1%3A0x5da4ed8f8d648c69!2sMumbai%2C%20Maharashtra!5e0!3m2!1sen!2sin!4v1641234567890!5m2!1sen!2sin"
                    width="100%"
                    height="100%"
                    style={{ border: 0 }}
                    allowFullScreen
                    loading="lazy"
                    title="NJR Exim Location"
                  />
                </div>
              </div>

              <div className="bg-primary text-primary-foreground p-8 rounded-2xl">
                <h3 className="text-2xl font-bold mb-4">Quick Response Guarantee</h3>
                <p className="text-primary-foreground/90 mb-6">
                  We understand the importance of timely communication in international trade.
                  Our team is committed to responding to all inquiries within 24 hours.
                </p>
                <ul className="space-y-3">
                  <li className="flex items-center gap-3">
                    <div className="w-2 h-2 rounded-full bg-golden" />
                    <span>Response within 24 hours</span>
                  </li>
                  <li className="flex items-center gap-3">
                    <div className="w-2 h-2 rounded-full bg-golden" />
                    <span>Dedicated account manager</span>
                  </li>
                  <li className="flex items-center gap-3">
                    <div className="w-2 h-2 rounded-full bg-golden" />
                    <span>Multilingual support available</span>
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* FAQ Link */}
      <section className="section-padding bg-muted">
        <div className="container-custom text-center">
          <h2 className="text-3xl md:text-4xl font-bold text-foreground mb-6">
            Have Questions Before Reaching Out?
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto mb-8">
            Check out our FAQ section for answers to common questions about our products,
            services, and export process.
          </p>
          <a
            href="/#faq"
            className="inline-flex items-center justify-center px-8 py-4 bg-primary text-primary-foreground rounded-full font-semibold hover:bg-primary/90 transition-all duration-300 hover:scale-105"
          >
            View FAQs
          </a>
        </div>
      </section>
    </Layout>
  );
};

export default Contact;

import Layout from "@/components/layout/Layout";
import SEO from "@/components/SEO";
import HeroSection from "@/components/home/HeroSection";
import AboutSection from "@/components/home/AboutSection";
import ProductsSection from "@/components/home/ProductsSection";
import FeaturesSection from "@/components/home/FeaturesSection";
import WhyUsSection from "@/components/home/WhyUsSection";
import VideoBanner from "@/components/home/VideoBanner";
import TestimonialsSection from "@/components/home/TestimonialsSection";
import FAQSection from "@/components/home/FAQSection";
import { useState, useEffect } from "react";
import api from "@/lib/api";
import Loader from "@/components/ui/Loader";

const Index = () => {
  const [content, setContent] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const { data } = await api.get("/pages/home");
        setContent(data.content);
      } catch (error) {
        console.error("Failed to fetch home content", error);
      } finally {
        setLoading(false);
      }
    };
    fetchContent();
  }, []);

  if (loading && !content) return <Loader />;

  return (
    <Layout>
      <SEO
        title="Premium Agricultural Export Company"
        description="NJR EXIM is a leading international exporter specializing in premium Indian agricultural products like Rice, Spices, Fruits, and Vegetables. Quality guaranteed."
        canonical="/"
      />
      <HeroSection content={content?.hero} />
      <AboutSection content={content?.aboutSection} />
      <ProductsSection />
      <FeaturesSection />
      <WhyUsSection />
      <VideoBanner />
      <TestimonialsSection />
      <FAQSection />
    </Layout>
  );
};

export default Index;

import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { toast } from "sonner";
import api from "@/lib/api";

const AdminLogin = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const userInfo = localStorage.getItem("userInfo");
        if (userInfo) {
            navigate("/admin/dashboard");
        }
    }, [navigate]);

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        try {
            const { data } = await api.post("/auth/login", { email, password });
            localStorage.setItem("userInfo", JSON.stringify(data));
            toast.success("Login successful");
            navigate("/admin/dashboard");
        } catch (error: any) {
            toast.error(error.response?.data?.message || "Invalid credentials");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-slate-50">
            <Card className="w-full max-w-md">
                <CardHeader className="space-y-1 text-center">
                    <CardTitle className="text-2xl font-bold">Admin Login</CardTitle>
                    <CardDescription>
                        Enter your credentials to access the CMS
                    </CardDescription>
                </CardHeader>
                <form onSubmit={handleLogin}>
                    <CardContent className="space-y-4">
                        <div className="space-y-2">
                            <Label htmlFor="email">Email</Label>
                            <Input
                                id="email"
                                type="email"
                                placeholder="admin@example.com"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div className="space-y-2">
                            <Label htmlFor="password">Password</Label>
                            <Input
                                id="password"
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                    </CardContent>
                    <CardFooter>
                        <Button className="w-full" type="submit" disabled={loading}>
                            {loading ? "Logging in..." : "Login"}
                        </Button>
                    </CardFooter>
                </form>
            </Card>
        </div>
    );
};

export default AdminLogin;

import React, { useState, useEffect } from "react";
import {
    Card,
    CardContent,
    CardHeader,
    CardTitle,
    CardDescription
} from "@/components/ui/card";
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogHeader,
    DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { toast } from "sonner";
import { Edit3, Globe, Save, RefreshCcw, Search, Eye, ArrowRight } from "lucide-react";
import api from "@/lib/api";
import { cn } from "@/lib/utils";
import Loader from "@/components/ui/Loader";

interface Page {
    _id: string;
    title: string;
    slug: string;
    content: any;
    isActive: boolean;
}

const PageManager = () => {
    const [pages, setPages] = useState<Page[]>([]);
    const [loading, setLoading] = useState(true);
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [editingPage, setEditingPage] = useState<Page | null>(null);
    const [pageContent, setPageContent] = useState<any>(null);
    const [searchQuery, setSearchQuery] = useState("");

    const fetchPages = async () => {
        setLoading(true);
        try {
            const { data } = await api.get("/pages");
            setPages(data);
        } catch (error) {
            toast.error("Failed to fetch pages");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchPages();
    }, []);

    const handleEditPage = (page: Page) => {
        setEditingPage(page);
        setPageContent(page.content);
        setIsDialogOpen(true);
    };

    const handleUpdateContent = (path: string, value: any) => {
        const newContent = { ...pageContent };
        const keys = path.split('.');
        let current = newContent;
        for (let i = 0; i < keys.length - 1; i++) {
            current = current[keys[i]];
        }
        current[keys[keys.length - 1]] = value;
        setPageContent(newContent);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            await api.put(`/pages/${editingPage?.slug}`, {
                content: pageContent
            });
            toast.success("Page content updated successfully");
            setIsDialogOpen(false);
            fetchPages();
        } catch (error) {
            toast.error("Failed to update page content");
        }
    };

    const renderEditorFields = (obj: any, prefix = "") => {
        return Object.keys(obj).map((key) => {
            const fullPath = prefix ? `${prefix}.${key}` : key;
            const value = obj[key];

            if (typeof value === "object" && value !== null && !Array.isArray(value)) {
                return (
                    <div key={fullPath} className="space-y-4 border-l-2 border-slate-100 pl-4 py-2">
                        <h4 className="text-sm font-bold uppercase tracking-wider text-indigo-600">{key}</h4>
                        {renderEditorFields(value, fullPath)}
                    </div>
                );
            }

            if (Array.isArray(value)) {
                return (
                    <div key={fullPath} className="space-y-4 border-l-2 border-indigo-100 pl-4 py-2 bg-slate-50/50 rounded-r-lg">
                        <h4 className="text-sm font-bold uppercase tracking-wider text-indigo-600">{key} (Array)</h4>
                        {value.map((item, index) => (
                            <div key={`${fullPath}.${index}`} className="p-3 bg-white border border-slate-200 rounded-lg shadow-sm space-y-3">
                                <div className="flex justify-between items-center">
                                    <span className="text-xs font-bold text-slate-400">Item #{index + 1}</span>
                                </div>
                                {typeof item === "object" ? renderEditorFields(item, `${fullPath}.${index}`) : (
                                    <Input
                                        value={item}
                                        onChange={(e) => handleUpdateContent(`${fullPath}.${index}`, e.target.value)}
                                    />
                                )}
                            </div>
                        ))}
                    </div>
                );
            }

            return (
                <div key={fullPath} className="space-y-1.5">
                    <Label className="text-xs font-semibold text-slate-500 capitalize">
                        {key.replace(/([A-Z])/g, ' $1')}
                    </Label>
                    {value.length > 100 || key.toLowerCase().includes('content') || key.toLowerCase().includes('description') ? (
                        <Textarea
                            value={value}
                            onChange={(e) => handleUpdateContent(fullPath, e.target.value)}
                            className="text-sm border-slate-200 focus:ring-indigo-500/20 min-h-[80px]"
                        />
                    ) : (
                        <Input
                            value={value}
                            onChange={(e) => handleUpdateContent(fullPath, e.target.value)}
                            className="text-sm border-slate-200 focus:ring-indigo-500/20"
                        />
                    )}
                </div>
            );
        });
    };

    const filteredPages = pages.filter(page =>
        page.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        page.slug.toLowerCase().includes(searchQuery.toLowerCase())
    );

    if (loading && pages.length === 0) return <Loader />;

    return (
        <div className="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-700">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div className="space-y-1">
                    <h1 className="text-3xl font-extrabold tracking-tight text-slate-900">Page Content Editor</h1>
                    <p className="text-slate-500 text-sm">Manage dynamic content for all public-facing pages.</p>
                </div>
                <Button onClick={fetchPages} variant="outline" className="gap-2">
                    <RefreshCcw className={cn("w-4 h-4", loading && "animate-spin")} /> Refresh
                </Button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredPages.map((page) => (
                    <Card key={page._id} className="group hover:shadow-xl transition-all duration-300 border-none shadow-sm overflow-hidden bg-white">
                        <CardHeader className="bg-slate-900 text-white pb-6 relative overflow-hidden">
                            <div className="absolute top-0 right-0 p-8 opacity-10 rotate-12">
                                <Globe size={80} />
                            </div>
                            <div className="relative z-10 flex flex-col gap-1">
                                <CardTitle className="text-xl font-bold">{page.title}</CardTitle>
                                <CardDescription className="text-slate-400 font-mono text-xs uppercase tracking-widest">
                                    /{page.slug === 'home' ? '' : page.slug}
                                </CardDescription>
                            </div>
                        </CardHeader>
                        <CardContent className="pt-6">
                            <div className="flex flex-col gap-4">
                                <div className="space-y-2">
                                    <p className="text-xs font-bold text-slate-400 uppercase">Sections Managed</p>
                                    <div className="flex flex-wrap gap-2">
                                        {Object.keys(page.content).map(section => (
                                            <span key={section} className="px-2 py-1 bg-slate-100 text-slate-600 rounded-md text-[10px] font-medium border border-slate-200">
                                                {section}
                                            </span>
                                        ))}
                                    </div>
                                </div>
                                <div className="pt-4 flex items-center justify-between border-t border-slate-50">
                                    <Button
                                        variant="default"
                                        onClick={() => handleEditPage(page)}
                                        className="w-full bg-indigo-600 hover:bg-indigo-700 gap-2"
                                    >
                                        <Edit3 size={16} /> Edit Content
                                    </Button>
                                </div>
                            </div>
                        </CardContent>
                    </Card>
                ))}
            </div>

            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogContent className="sm:max-w-4xl max-h-[90vh] overflow-hidden p-0 border-none shadow-2xl">
                    <div className="bg-slate-900 px-8 py-6 flex items-center justify-between shrink-0">
                        <div className="space-y-1">
                            <DialogTitle className="text-white text-2xl font-bold flex items-center gap-3">
                                <Globe className="text-indigo-400" />
                                Editing {editingPage?.title}
                            </DialogTitle>
                            <DialogDescription className="text-slate-400">
                                Update the textual and visual content for this page. Changes will reflect immediately on the site.
                            </DialogDescription>
                        </div>
                    </div>
                    <form onSubmit={handleSubmit} className="flex flex-col h-full overflow-hidden bg-white">
                        <div className="flex-1 overflow-y-auto p-8 space-y-8 scrollbar-thin">
                            {pageContent && renderEditorFields(pageContent)}
                        </div>
                        <div className="shrink-0 px-8 py-4 bg-slate-50 border-t border-slate-200 flex items-center justify-end gap-3">
                            <Button type="button" variant="ghost" onClick={() => setIsDialogOpen(false)}>
                                Cancel
                            </Button>
                            <Button type="submit" className="bg-indigo-600 hover:bg-indigo-700 px-8 gap-2">
                                <Save size={18} /> Save All Changes
                            </Button>
                        </div>
                    </form>
                </DialogContent>
            </Dialog>
        </div>
    );
};

export default PageManager;

import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { toast } from "sonner";
import api from "@/lib/api";

const Setup = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSetup = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        try {
            const { data } = await api.post("/auth/setup", { name, email, password });
            localStorage.setItem("userInfo", JSON.stringify(data));
            toast.success("Super Admin created successfully!");
            navigate("/admin/dashboard");
        } catch (error: any) {
            toast.error(error.response?.data?.message || "Setup failed. Maybe it's already done?");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="flex items-center justify-center min-h-screen bg-indigo-50">
            <Card className="w-full max-w-md border-t-4 border-indigo-600">
                <CardHeader className="space-y-1 text-center">
                    <CardTitle className="text-2xl font-bold">Initial System Setup</CardTitle>
                    <CardDescription>
                        Create the first Super Admin account to get started
                    </CardDescription>
                </CardHeader>
                <form onSubmit={handleSetup}>
                    <CardContent className="space-y-4">
                        <div className="space-y-2">
                            <Label htmlFor="name">Full Name</Label>
                            <Input
                                id="name"
                                placeholder="Admin Name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                            />
                        </div>
                        <div className="space-y-2">
                            <Label htmlFor="email">Email</Label>
                            <Input
                                id="email"
                                type="email"
                                placeholder="admin@example.com"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div className="space-y-2">
                            <Label htmlFor="password">Password</Label>
                            <Input
                                id="password"
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                    </CardContent>
                    <CardFooter>
                        <Button className="w-full bg-indigo-600 hover:bg-indigo-700" type="submit" disabled={loading}>
                            {loading ? "Creating..." : "Complete Setup"}
                        </Button>
                    </CardFooter>
                </form>
            </Card>
        </div>
    );
};

export default Setup;

import React, { useState, useEffect } from "react";
import {
    Table, TableBody, TableCell, TableHead, TableHeader, TableRow
} from "@/components/ui/table";
import {
    Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { toast } from "sonner";
import {
    Trash,
    Edit,
    Users,
    ShieldAlert,
    ShieldCheck,
    User,
    Mail,
    Calendar,
    Search,
    MoreVertical,
    UserPlus,
    UserCog,
    Fingerprint
} from "lucide-react";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuSeparator,
    DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import api from "@/lib/api";
import { cn } from "@/lib/utils";

interface UserInfo {
    _id: string;
    name: string;
    email: string;
    role: string;
    createdAt: string;
}

const UserManagement = () => {
    const [users, setUsers] = useState<UserInfo[]>([]);
    const [loading, setLoading] = useState(true);
    const [isDialogOpen, setIsDialogOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState("");
    const [editingUser, setEditingUser] = useState<UserInfo | null>(null);
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        role: "admin",
    });

    const fetchUsers = async () => {
        try {
            const { data } = await api.get("/auth/users");
            setUsers(data);
        } catch (error) {
            toast.error("Access Denied: Only Super Admins can manage users.");
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    const handleOpenDialog = (user: UserInfo) => {
        setEditingUser(user);
        setFormData({
            name: user.name,
            email: user.email,
            role: user.role,
        });
        setIsDialogOpen(true);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            if (editingUser) {
                await api.put(`/auth/users/${editingUser._id}`, formData);
                toast.success("User permissions updated");
                setIsDialogOpen(false);
                fetchUsers();
            }
        } catch (error) {
            toast.error("Failed to update user");
        }
    };

    const handleDelete = async (id: string) => {
        if (window.confirm("Are you sure you want to revoke access for this user?")) {
            try {
                await api.delete(`/auth/users/${id}`);
                toast.success("User account removed");
                fetchUsers();
            } catch (error: any) {
                toast.error(error.response?.data?.message || "Failed to delete user");
            }
        }
    };

    const filteredUsers = users.filter(user =>
        user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        user.email.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="space-y-8 animate-in fade-in slide-in-from-bottom-4 duration-700">
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div className="space-y-1">
                    <h1 className="text-3xl font-extrabold tracking-tight text-slate-900">User Governance</h1>
                    <div className="flex items-center gap-2 text-slate-500 text-sm">
                        <ShieldAlert className="w-4 h-4 text-indigo-600" />
                        <span>Manage administrative access and role assignments.</span>
                    </div>
                </div>
                <div className="flex items-center gap-2">
                    <Badge variant="outline" className="bg-indigo-50/50 text-indigo-600 border-indigo-100 hidden sm:flex">
                        {users.length} Active Personnel
                    </Badge>
                </div>
            </div>

            <Card className="border-none shadow-sm overflow-hidden bg-white/50 backdrop-blur-sm">
                <CardHeader className="pb-0 border-b border-slate-100 bg-white/80">
                    <div className="flex items-center gap-4 pb-4">
                        <div className="relative flex-1 max-w-sm group">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-400 group-focus-within:text-indigo-500 transition-colors" />
                            <Input
                                placeholder="Search by name or email..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="pl-9 bg-slate-50 border-slate-200 focus-visible:ring-indigo-500/30 font-medium"
                            />
                        </div>
                    </div>
                </CardHeader>
                <CardContent className="p-0">
                    <Table>
                        <TableHeader>
                            <TableRow className="bg-slate-50/50 hover:bg-slate-50/50 border-slate-100">
                                <TableHead className="font-bold text-slate-700">Administrator</TableHead>
                                <TableHead className="font-bold text-slate-700">Email Address</TableHead>
                                <TableHead className="font-bold text-slate-700">Access Level</TableHead>
                                <TableHead className="font-bold text-slate-700">Registration</TableHead>
                                <TableHead className="text-right font-bold text-slate-700 px-6">Control</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody>
                            {loading ? (
                                Array(3).fill(0).map((_, i) => (
                                    <TableRow key={i} className="animate-pulse">
                                        <TableCell><div className="h-10 bg-slate-100 rounded w-40"></div></TableCell>
                                        <TableCell><div className="h-4 bg-slate-100 rounded w-48"></div></TableCell>
                                        <TableCell><div className="h-6 bg-slate-100 rounded w-20"></div></TableCell>
                                        <TableCell><div className="h-4 bg-slate-100 rounded w-20"></div></TableCell>
                                        <TableCell className="text-right"><div className="h-8 bg-slate-100 rounded-full w-8 ml-auto"></div></TableCell>
                                    </TableRow>
                                ))
                            ) : filteredUsers.length === 0 ? (
                                <TableRow>
                                    <TableCell colSpan={5} className="h-64 text-center">
                                        <div className="flex flex-col items-center justify-center text-slate-400">
                                            <Users size={48} className="mb-2 opacity-20" />
                                            <p className="font-medium">No users found</p>
                                            <p className="text-xs">Try a different search term.</p>
                                        </div>
                                    </TableCell>
                                </TableRow>
                            ) : (
                                filteredUsers.map((user) => (
                                    <TableRow key={user._id} className="group hover:bg-slate-50/80 transition-colors border-slate-50">
                                        <TableCell>
                                            <div className="flex items-center gap-3">
                                                <div className={cn(
                                                    "w-10 h-10 rounded-full flex items-center justify-center font-bold text-xs ring-2 ring-white shadow-sm",
                                                    user.role === 'super-admin'
                                                        ? "bg-indigo-600 text-white"
                                                        : "bg-slate-200 text-slate-600"
                                                )}>
                                                    {user.name.charAt(0).toUpperCase()}
                                                </div>
                                                <span className="font-bold text-slate-900">{user.name}</span>
                                            </div>
                                        </TableCell>
                                        <TableCell>
                                            <div className="text-slate-500 font-medium flex items-center gap-2">
                                                <Mail className="w-3.5 h-3.5" />
                                                {user.email}
                                            </div>
                                        </TableCell>
                                        <TableCell>
                                            <Badge
                                                variant="outline"
                                                className={cn(
                                                    "font-bold px-2 py-0.5 h-6 text-[10px] uppercase border",
                                                    user.role === 'super-admin'
                                                        ? "bg-indigo-50 text-indigo-700 border-indigo-100"
                                                        : "bg-slate-100 text-slate-600 border-slate-200"
                                                )}
                                            >
                                                {user.role === 'super-admin' ? (
                                                    <ShieldCheck className="w-3 h-3 mr-1" />
                                                ) : (
                                                    <ShieldAlert className="w-3 h-3 mr-1 opacity-50" />
                                                )}
                                                {user.role}
                                            </Badge>
                                        </TableCell>
                                        <TableCell>
                                            <div className="text-slate-500 text-xs flex items-center gap-1.5 font-medium">
                                                <Calendar className="w-3.5 h-3.5" />
                                                {new Date(user.createdAt).toLocaleDateString()}
                                            </div>
                                        </TableCell>
                                        <TableCell className="text-right px-6">
                                            <DropdownMenu>
                                                <DropdownMenuTrigger asChild>
                                                    <Button variant="ghost" size="icon" className="h-8 w-8 hover:bg-slate-200">
                                                        <MoreVertical className="h-4 w-4 text-slate-500" />
                                                    </Button>
                                                </DropdownMenuTrigger>
                                                <DropdownMenuContent align="end" className="w-48">
                                                    <DropdownMenuItem onClick={() => handleOpenDialog(user)} className="cursor-pointer">
                                                        <Edit className="mr-2 h-4 w-4" /> Edit Permissions
                                                    </DropdownMenuItem>
                                                    {user.role !== 'super-admin' && (
                                                        <>
                                                            <DropdownMenuSeparator />
                                                            <DropdownMenuItem onClick={() => handleDelete(user._id)} className="cursor-pointer text-red-600 focus:text-red-700 focus:bg-red-50">
                                                                <Trash className="mr-2 h-4 w-4" /> Revoke Access
                                                            </DropdownMenuItem>
                                                        </>
                                                    )}
                                                </DropdownMenuContent>
                                            </DropdownMenu>
                                        </TableCell>
                                    </TableRow>
                                ))
                            )}
                        </TableBody>
                    </Table>
                </CardContent>
            </Card>

            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
                <DialogContent className="sm:max-w-md overflow-hidden p-0 border-none shadow-2xl">
                    <div className="bg-slate-900 px-8 py-6 flex items-center justify-between">
                        <div className="space-y-1">
                            <DialogTitle className="text-white flex items-center gap-2 text-xl font-extrabold tracking-tight">
                                <UserCog className="w-5 h-5 text-indigo-400" />
                                Account Setting
                            </DialogTitle>
                            <DialogDescription className="text-slate-400 text-xs text-balance font-medium">
                                Update administrator privileges and profile information.
                            </DialogDescription>
                        </div>
                    </div>

                    <form onSubmit={handleSubmit} className="p-8 space-y-6 bg-white">
                        <div className="space-y-4">
                            <div className="space-y-2">
                                <Label className="text-[10px] font-bold uppercase tracking-widest text-slate-500 flex items-center gap-2">
                                    <User className="w-3 h-3" /> Full Identity
                                </Label>
                                <Input
                                    value={formData.name}
                                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                    required
                                    className="border-slate-200 focus:ring-indigo-500/20 h-11 bg-slate-50/50 font-medium"
                                />
                            </div>
                            <div className="space-y-2">
                                <Label className="text-[10px] font-bold uppercase tracking-widest text-slate-500 flex items-center gap-2">
                                    <Mail className="w-3 h-3" /> Communication Email
                                </Label>
                                <Input
                                    type="email"
                                    value={formData.email}
                                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                    required
                                    className="border-slate-200 focus:ring-indigo-500/20 h-11 bg-slate-50/50 font-medium"
                                />
                            </div>
                            <div className="space-y-2">
                                <Label className="text-[10px] font-bold uppercase tracking-widest text-slate-500 flex items-center gap-2">
                                    <Fingerprint className="w-3 h-3" /> Security Role Assignment
                                </Label>
                                <Select
                                    value={formData.role}
                                    onValueChange={(value) => setFormData({ ...formData, role: value })}
                                >
                                    <SelectTrigger className="border-slate-200 focus:ring-indigo-500/20 h-11 bg-slate-50/50 font-bold">
                                        <SelectValue placeholder="Select role" />
                                    </SelectTrigger>
                                    <SelectContent>
                                        <SelectItem value="admin" className="font-medium">
                                            Standard Administrator
                                        </SelectItem>
                                        <SelectItem value="super-admin" className="font-bold text-indigo-600">
                                            Super Administrator
                                        </SelectItem>
                                    </SelectContent>
                                </Select>
                                <p className="text-[10px] text-slate-400 mt-2 italic px-1">
                                    * Super admins have full access to user governance and system settings.
                                </p>
                            </div>
                        </div>

                        <div className="flex flex-col gap-3 pt-4 border-t border-slate-100">
                            <Button type="submit" className="w-full bg-slate-900 hover:bg-slate-800 text-white h-12 font-extrabold shadow-xl shadow-slate-900/20 text-sm transition-all focus:scale-[0.98]">
                                <ShieldCheck className="w-4 h-4 mr-2" />
                                Commit Permission Changes
                            </Button>
                            <Button type="button" variant="ghost" onClick={() => setIsDialogOpen(false)} className="w-full text-slate-400 h-10 text-xs font-bold tracking-widest uppercase hover:bg-slate-50">
                                Discard Edit
                            </Button>
                        </div>
                    </form>
                </DialogContent>
            </Dialog>
        </div>
    );
};

export default UserManagement;

